import React, { useState } from "react";
import { SliderData } from "./SliderData";
import monsoonGraphic from "./595198868_505x160.jpeg";
import beamsGraphic from "./589972810_530x315.jpeg";
import moveGraphic from "./590587169_530x315.jpeg";
import playIcon from "./play-icon.svg";
import leftArrow from "./left-arrow.svg";
import rightArrow from "./right-arrow.svg";
import { TransitionGroup, CSSTransition } from "react-transition-group";

const ImageSlider = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const length = slides.length;
  const [direction, setDirection] = useState("right");

  const slideLeft = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
    setDirection(direction = "left");
  };
  const slideRight = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
    setDirection(direction = "right");
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  return (
    <>
      {SliderData.map((slide, index) => {
        return (
          <>
            <div className = "arrows">
              <img
                src={leftArrow}
                alt="left-arrow"
                className="left-arrow"
                onClick={slideLeft}
              />
              <img
                src={rightArrow}
                alt="right-arrow"
                className="right-arrow"
                onClick={slideRight}
              />
            </div>
            <div
              className={(index === current) ? "slide-active" : "slide"}
              >
              {index === current && (
                <>
                  <div className="slider">
                    <img src={slide.image} alt="poster" className="bg-image" />
                    <div class="bg-darken" />
                    <div
                      class="bg-color"
                      style={{ backgroundColor: slide.color + "44" }}
                    />
                    <div class="movie">
                      <div class="movie-image-fade">
                        <div class="movie-image-glow">
                          <img
                            src={slide.image}
                            alt="movie poster"
                            className="movie-image"
                          />
                        </div>
                      </div>
                      <div class="movie-information">
                        <h2 className="slider-title">{slide.title}</h2>
                        <p className="slider-description">
                          {slide.description}
                        </p>
                        <div className = "buttons">
                        <a href={slide.purchase} className="button-link">
                          <button
                            class="buy-now"
                            style={{ backgroundColor: slide.color }}
                          >
                            <img
                              src={playIcon}
                              alt="play-icon"
                              className="play-icon"
                            />
                            Buy Now
                          </button>
                        </a>
                        <a href={slide.trailer} className="button-link">
                          <button class="watch-trailer">Watch Trailer</button>
                        </a>
                        </div>
                      </div>
                    </div>
                  </div>
                </>
              )}
            </div>
          </>
        );
      })}

      <body>
        {/* <!-- <div class="example">
        <iframe src="https://player.vimeo.com/video/186000920" width="640" height="400" frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>
    </div> --> */}

        <div class="monsoon">
          <div class="container-right">
            <h1>Monsoon III</h1>
            <p className="feature-description">
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec
              tincidunt ipsum augue. In faucibus vehicula magna pulvinar
              aliquam. Cras aliquan feugiat lorem non auctor. Quisque sed lorem
              egestas mauris venenatis commodo eu id nibh. Ut porta libero sed
              semper faucibus.
            </p>
          </div>
          <div class="container-left">
            <img src={monsoonGraphic} alt="graphic" className="feature-image" />
            {/* <!-- <iframe src="https://player.vimeo.com/video/186000920" frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe> --> */}
          </div>
        </div>

        <div class="beams">
          <div class="container-left">
            <h1>Beams</h1>
            <p className="feature-description">
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec
              tincidunt ipsum augue. In faucibus vehicula magna pulvinar
              aliquam. Cras aliquan feugiat lorem non auctor. Quisque sed lorem
              egestas mauris venenatis commodo eu id nibh. Ut porta libero sed
              semper faucibus.
            </p>
          </div>
          <div class="container-right">
            <img src={beamsGraphic} alt="graphic" className="feature-image" />
          </div>
        </div>

        <div class="move">
          <div class="container-right">
            <h1>Move 2</h1>
            <p className="feature-description">
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec
              tincidunt ipsum augue. In faucibus vehicula magna pulvinar
              aliquam. Cras aliquan feugiat lorem non auctor. Quisque sed lorem
              egestas mauris venenatis commodo eu id nibh. Ut porta libero sed
              semper faucibus.
            </p>
          </div>
          <div class="container-left">
            <img src={moveGraphic} alt="graphic" className="feature-image" />
          </div>
        </div>
      </body>
    </>
  );
};

export default ImageSlider;
