import React, { useState } from "react";
import { SliderData } from "./SliderData";
import { FaArrowAltCircleRight, FaArrowAltCircleLeft } from "react-icons/fa";
import monsoonGraphic from "./595198868_505x160.jpeg";
import beamsGraphic from "./589972810_530x315.jpeg";
import moveGraphic from "./590587169_530x315.jpeg";

const ImageSlider = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const length = slides.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };
  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  // var sliderBackground = {
  //   backgroundImage:
  //     "url('https://images.pexels.com/photos/34153/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350')",
  //   backgroundPosition: "center",
  //   width: '100%',
  //   backgroundSize: "cover",
  //   backgroundRepeat: "no-repeat",
  // };

  return (
    <>
      {SliderData.map((slide, index) => {
        return (
          <>
            {/* <div style=""> */}
            <div
              className={index === current ? "slide-active" : "slide"}
              key={index}
              // style={sliderBackground}
            >
              {index === current && (
                <>
                  {/* */}
                  <div className="slider">
                    {/* <div class="blur-bg">
                      <img src={slide.image} alt="poster" className="bg-image" />
                    </div>  */}
                    <FaArrowAltCircleLeft
                      className="left-arrow"
                      onClick={prevSlide}
                    />
                    <FaArrowAltCircleRight
                      className="right-arrow"
                      onClick={nextSlide}
                    />

                    <div class="container-left">
                      <img src={slide.image} alt="poster" className="image" />
                    </div>
                    <div class="container-right">
                      <h2 className="slider-title">{slide.title}</h2>
                      <p className="slider-description">{slide.description}</p>
                      <a href={slide.purchase}>
                        <button>Buy Now</button>
                      </a>
                      <a href={slide.trailer}>
                        <button>Watch Trailer</button>
                      </a>
                    </div>
                  </div>
                </>
              )}
            </div>
            {/* </div> */}
          </>
        );
      })}

      {/* <section className="slider">

      </section> */}

    </>
  );
};

export default ImageSlider;
