import React, { useState } from "react";
import { SliderData } from "./SliderData";
import { FaArrowAltCircleRight, FaArrowAltCircleLeft, FaBluetooth } from "react-icons/fa";
import monsoonGraphic from './595198868_505x160.jpeg';
import beamsGraphic from './589972810_530x315.jpeg';
import moveGraphic from './590587169_530x315.jpeg';

const ImageSlider = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const length = slides.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };
  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  var sliderBackground = {  
    backgroundImage: "url('https://images.pexels.com/photos/34153/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350')",
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat'
  }

  return (
    <>
    <section className="slider">
      <FaArrowAltCircleLeft className="left-arrow" onClick={prevSlide} />
      <FaArrowAltCircleRight className="right-arrow" onClick={nextSlide} />
      {SliderData.map((slide, index) => {
        return (
            <>
            {/* <div style=""> */}
          <div className={index === current ? 'slide-active' : 'slide'}
            key={index} style={sliderBackground}>
            {index === current && (
              <>
              <div class = "container-left">
                <img src={slide.image} alt="poster" className="image" />
              </div>
              <div class = "container-right">
                <h2 className = "slider-title">{slide.title}</h2>
                <p className = "slider-description">{slide.description}</p>
                <a href = {slide.purchase}><button>Buy Now</button></a>
                <a href = {slide.trailer}><button>Watch Trailer</button></a>
              </div>
              </>
            )}
          </div>
          {/* </div> */}
          </>
        );
      })}
    </section>
    <body>
    {/* <!-- <div class="example">
        <iframe src="https://player.vimeo.com/video/186000920" width="640" height="400" frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>
    </div> --> */}

    <div class="monsoon">
      <div class="container-right">
        <h1>Monsoon III</h1>
        <p className = "feature-description">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec
          tincidunt ipsum augue. In faucibus vehicula magna pulvinar aliquam.
          Cras aliquan feugiat lorem non auctor. Quisque sed lorem egestas
          mauris venenatis commodo eu id nibh. Ut porta libero sed semper
          faucibus.
        </p>
      </div>
      <div class="container-left">
        <img src={monsoonGraphic} alt="graphic"/>
        {/* <!-- <iframe src="https://player.vimeo.com/video/186000920" frameborder="0" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe> --> */}
      </div>
    </div>

    <div class="beams">
      <div class="container-left">
        <h1>Beams</h1>
        <p className = "feature-description">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec
          tincidunt ipsum augue. In faucibus vehicula magna pulvinar aliquam.
          Cras aliquan feugiat lorem non auctor. Quisque sed lorem egestas
          mauris venenatis commodo eu id nibh. Ut porta libero sed semper
          faucibus.
        </p>
      </div>
      <div class="container-right">
        <img src={beamsGraphic} alt="graphic"/>
      </div>
    </div>

    <div class="move">
      <div class="container-right">
        <h1>Move 2</h1>
        <p className = "feature-description">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec
          tincidunt ipsum augue. In faucibus vehicula magna pulvinar aliquam.
          Cras aliquan feugiat lorem non auctor. Quisque sed lorem egestas
          mauris venenatis commodo eu id nibh. Ut porta libero sed semper
          faucibus.
        </p>
      </div>
      <div class="container-left">
        <img src={moveGraphic} alt="graphic"/>
      </div>
    </div>
  </body>
    </>
  );
};

export default ImageSlider;
